import React, { useState } from 'react';
import './index.css';
import './styles.css';

export default function ListingAd({ pic, title, address, description, psf_min, psf_max, project_type, year, ownership_type, subprice_label, availabilities_label }) {
  const [showDescription, setShowDescription] = useState(false);
  const [maskedDescription, setMaskedDescription] = useState(description);

  const handleSeeDescriptionClick = () => {
    setShowDescription(!showDescription);
  };

  useEffect(() => {
    // Mask phone numbers with "XXXX" when showing description
    const maskedPhoneNumber = description.replace(/\b\d{8}\b/g, 'XXXX');
    setMaskedDescription(maskedPhoneNumber);
  }, [description, showDescription]);


  return (
      <div className="App p-4">
        <div className='card'>
          <img src={pic} style={{height: '272px', width: '544px' }}/>
          <div className='wrapper flex flex-row p-4'>
              <div className='text-start flex flex-col'>
                <div className='flex flex-row mt-1'>
                  <img className='mt-2' style={{ width: 40, height: 40 }} src={pic} />
                  <div className='flex flex-col ml-2 '>
                  <div className='avenir-demibold f-23 f-color2'>{title}</div>
                  <div className='avenir-regular f-14 f-color1'>{address}</div>
               </div>
                </div>
                <div className='mt-2 text-start flex flex-row avenir-regular f-16 f-color2'>
                  {project_type} - {year} - {ownership_type}
                </div>
                <div className='text-start flex flex-row avenir-regular f-16 f-color2'>
                  {availabilities_label}
               </div>
              </div>   
            <div className=' col-2 text-end flex flex-col'>
                <div className='avenir-demibold' style={{fontSize: '19px'}}>${psf_min} - {psf_max} psf</div>
                <div className='avenir-regular f-14 f-color1 text-1'>{subprice_label}</div>
            </div>
          </div>
          <div className='pb-4 pr-4 pl-4 text-end avenir-demibold f-16 f-color3' >
          <button className='' onClick={handleSeeDescriptionClick}>
            {showDescription ? 'Show original description' : 'See description'}
          </button>
          {showDescription &&
            <div className='text-center'>
              {maskedDescription}
            </div>}
        </div>
        </div>
      </div>
  );
}
