import React, { useState } from 'react';
import './index.css';
import './styles.css';

export default function ListingAd({
  pic,
  title,
  address,
  description,
  psf_min,
  psf_max,
  project_type,
  year,
  ownership_type,
  subprice_label,
  availabilities_label,
}) {
  const [showDescription, setShowDescription] = useState(false);

  const handleSeeDescriptionClick = () => {
    setShowDescription(!showDescription);
  };

  // Function to anonymize phone numbers
  const anonymizePhoneNumber = (phoneNumber) => {
    return phoneNumber.slice(0, 4) + 'XXXX';
  };

  // Anonymize phone numbers in the description
  const anonymizedDescription = description.replace(
    /\b\d{4}\s\d{4}\b/g,
    (match) => anonymizePhoneNumber(match)
  );

  return (
    <div className="App p-4">
      <div className="card">
        <img src={pic} style={{ height: '272px', width: '544px' }} />
        <div className="wrapper flex flex-row p-4">
          <div className="text-start flex flex-col">
            {/* ... rest of the code ... */}
          </div>
          <div className="col-2 text-end flex flex-col">
            {/* ... rest of the code ... */}
          </div>
        </div>
        <div className="pb-4 pr-4 text-end avenir-demibold f-16 f-color3">
          <button className="" onClick={handleSeeDescriptionClick}>
            See description
          </button>
          {showDescription && <div>{anonymizedDescription}</div>}
        </div>
      </div>
    </div>
  );
}
